search_c:
int search_c(int data) {
    struct node *temp = start;
    int pos = 0;
    if (start == NULL) {
        return -1;
    }
    do {
        if (temp->data == data) {
            return pos;
        }
        temp = temp->next;
        pos++;
    } while (temp != start);
    return -2;
}

count_c:
int count_c() {
    if (start == NULL) {
        return 0;
    }
    struct node *temp = start;
    int count = 0;
    do {
        temp = temp->next;
        count++;
    } while(temp != start);
    return count;
}
