void insertBefore_dl(int data, int elem) {
	int pos = search(elem);
	if (pos == -1 || pos == -2) {
		printf("Operation cannot be performed.");
		return;
	}
	struct node *ptr = (struct node*)malloc(sizeof(struct node));
	if (ptr == NULL) {
		printf("Memory Allocation Failed.");
		return;
	}
	ptr->data = data;
	ptr->next = NULL;
	ptr->prev = NULL;
	if (pos == 0) {
		ptr->next = start;
		start->prev = ptr;
		start = ptr;
		return;
	}
	struct node *temp = start;
	for (int i = 0; i < pos - 1; i++) {
		temp = temp->next;
	}
	ptr->prev = temp;
	ptr->next = temp->next;
	temp->next->prev = ptr;
	temp->next = ptr;
}

