search_l:
int search_l(int data) {
    struct node *temp = start;
    int pos = 0;
    if (start == NULL) {
        return -1;
    }
    while (temp != NULL) {
        if (temp->data == data)  {
            return pos;
        }
        temp = temp->next;
        pos++;
    }
    return -2;
}

count_l:
int count_l() {
    struct node *temp = start;
    int count = 0;
    while (temp != NULL) {
        temp = temp->next;
        count++;
    }
    return count;
}
