Search:
int search(int data) {
	struct node* temp = start;
	int pos = -1;
	if (temp == NULL) {
		return pos;
	}
	while (temp != NULL) {
		if (temp->data == data) {
			return ++pos;
		} else {
			temp = temp->next;
			pos++;
		}
	}
	return -2;
}

Count:
int count() {
	struct node *temp = start;
	int count = 0;
	while (temp != NULL) {
		temp = temp->next;
		count++;
	}
	return count;
}
